import React, {Component} from 'react'
import {storeProducts, detailProduct} from './data'


const ProductContext = React.createContext()

class ProductProvider extends Component {

    state = {
        products: [],
        detailproducts: detailProduct,
        inCart : []
    };

    componentDidMount() {
        this.setProducts()
    }

    setProducts = () => {
        let products = []
        storeProducts.forEach( item => {
                const update = {...item}
                products = [...products, update ]
        } );
        this.setState( () => {
            return {products: products}
        } )
    }

    handlegetitem = id => {
        const prodcutId = this.state.products.find(item => item.id === id);
        return prodcutId
    }

    handleDetail = (id) => {

        const product = this.handlegetitem(id)
        this.setState( () => {
            return {detailproducts: product}
        })
    }
    handleaddTocart = (id) => {
        let tempProducts = {...this.state.products}
        const productIndex = tempProducts.indexOf(this.handlegetitem(id));
        const product = tempProducts[productIndex]
        product.count = 1;
        product.inCart = true
        const price = product.price;
        product.total = price
        this.setState(() => {
            return { products: tempProducts, inCart: [...this.state.inCart] }
        })



    }

    render() {
        return (

            <ProductContext.Provider value={{
                ...this.state,
                handleDetail: this.handleDetail,
                handleaddTocart: this.handleaddTocart
            }}>
                {this.props.children}
            </ProductContext.Provider>

        )
    }
}

const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer}
