import React, {Component } from 'react'
import styled from 'styled-components'
import { Link } from 'react-router-dom'
import {ProductConsumer} from '../context'
import {storeProducts, detailProduct} from '../data'


class Product extends Component {

    render() {
        const { id, title, img, price, inCart } = this.props.Product
        const {history} = this.props;
        console.log(this.context.history)
        return (
            <ProductWarp className="col-9 mx-auto col-md-6 col-lg-3 my-3">
                <div className="card">
                <div className="img-container p-5">
                    <Link to="/detail">
                        <img src={img} alt="product" className="card-img-top" />
                    </Link>
                    <button className="cart-btn" disabled={inCart ? true: false} >
                        {inCart ? (<p className="text-captialize mb-0" disabled>inCart</p>) : 
                    ( <i className="fas fa-cart-plus" />)
                    }
                    </button>


                </div>
                <div className="card-footer d-flex justify-content-between">
                    <p className="align-self-center mb-0"> {title} </p>
                    <h5 className="text-blue font-italic mb-0">
                        <span className="mr-1">$</span>
                        {price}
                    </h5>
                    </div> 
                </div>

                {storeProducts.map ( it => {
                    return (
                        <React.Fragment>
                        <h1 onClick={() => this.props.history.push('/detail/${it.id}')}>{it.title}</h1>
                        </React.Fragment>

                    )
                })}


            </ProductWarp>
        )
    }
}


export default Product;

const ProductWarp = styled.div `

.card {
    border-color : transparent;
    transition: all 1s linear;
}

.card-footer {
    background: transparent;
    border-top : transparent;
    transition: all 1s linear;

}
&:hover {
    .card {
        border: 0.04rem solid rgba(0, 0, 0, 0.2);
        box-shadow: 2px 5px 0px rgba(0, 0, 0, 0.2);
    }
    .card-footer {
        background: rgba(247, 247, 247);
    }
}

.img-container {
    position : relative;
    overflow: hidden;
}

.card-img-top {
    transition: all 1s linear
}

.img-container:hover .card-img-top {
    transform: scale(1.2);
}

.cart-btn {
    position: absolute;
    bottom: 0;
    right: 0;
    padding: 0.2rem 0.4rem;
    background: var(--lightBlue);
    border : none;
    color: var(--mainWhite);
    font-size : 1.4rem;
    border-radius: 0.5 0 0 0
    transform : translate(100%, 100%)

}
.img-container:hover .cart-btn {
    transform: translate(0,0)
}

.cart-btn:hover {
    color: var(--mainBlue);
    cursor: pointer;
}


`;
